select leaid, leaname, leaphonenum, leaemail from ( select /*+INDEX_DESC(leader SYS_C007263)*/ rownum rn, leaid, leaname, leaphonenum, leaemail, leastate from leader where
leaId like '%' || #{keyword} || '%'
rownum <= #{pageNum} * #{amount} and leastate = '가입완료' and enabled = '1' ) where rn > (#{pageNum} -1) * #{amount}

select leaid, leaname, leaphonenum, leaemail, leareqdate from ( select /*+INDEX_DESC(leader SYS_C007263)*/ rownum rn, leaid, leaname, leaphonenum, leaemail, leareqdate, leastate from leader where
leaId like '%' || #{keyword} || '%'
rownum <= #{pageNum} * #{amount} and leastate = '가입대기' ) where rn > (#{pageNum} -1) * #{amount}

select leaid, leaname, leaphonenum, leaemail, leareqdate from ( select /*+INDEX_DESC(leader SYS_C007263)*/ rownum rn, leaid, leaname, leaphonenum, leaemail, leareqdate, leastate from leader where
leaId like '%' || #{keyword} || '%'
rownum <= #{pageNum} * #{amount} and leastate = '가입거부') where rn > (#{pageNum} -1) * #{amount}

